k1 = bitmap.map {|line| line.reverse }.join.tr(" #", "01").to_i(2)

raise if k1.to_s.size % 60 != 58

font = 731024063570851866511465264858993450227206304418681785374039 * 2

k1.to_s[0, 58].each_char.with_index do |c, i|
  5.times do |y|
    4.times do |x|
      bitmap[-5 + y][i * 4 + x + 8] = font[c.to_i * 20 + y * 4 + x] == 1 ? "#" : " "
    end
  end
end

k2 = bitmap.map {|line| line.reverse }.join.tr(" #", "01").to_i(2)

raise if k1.to_s[0, 58] != k2.to_s[0, 58]

puts <<END
main = putStrLn $ show a
a = b ++ c
b = [(x,  y+1) | x<-i(0,240), y<-i(0,59), g(kv,240*y+x,2)==1 ]
c = [(x, -y-1) | x<-i(0,240), y<-i(0,419), f(x,y)==1 ]
i(x,y) = [x..y-1]
f(x,y) = g(g(2*fv,g(kv,(4199-60*(div y 6)-(div x 4)),10),1048576),4*(mod y 6)+(mod x 4),2)
g(k,x,b) = mod (k `div` (b^x)) b
fv = 731024063570851866511465264858993450227206304418681785374039
kv = #{ k2 }::Integer
END
